export const MoniteProvider = ({
  apiUrl,
  entityId,
  entityUserId,
  children,
}: {
  apiUrl: string;
  entityId: string;
  entityUserId: string;
  children: ReactNode;
}) => {
  const { i18n } = useLingui();

  const fetchToken = useCallback(async () => {
    /**
     * We must add `entityUserId` as a dependency to create a new `fetchToken`
     * function and call create a new `MoniteSDK` instance.
     * Whenever `monite` is updated, all the components that depend on it will
     * be re-rendered, and data will be fetched again.
     */
    if (!entityUserId) throw new Error('entityUserId is not defined');

    return (
      await fetch('/api/auth/token', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        cache: 'no-cache',
      })
    ).json();
  }, [entityUserId]);

  const monite = useMemo(
    () => ({
      apiUrl,
      entityId,
      fetchToken,
    }),
    [apiUrl, entityId, fetchToken]
  );

  return (
    <MoniteProviderBase
      monite={monite}
      componentSettings={{
        receivables: {
          tabs: [
            {
              label: 'Invoices',
              query: {
                type: 'invoice',
              },
            },
            {
              label: 'My Financing',
              query: {
                type: 'financing',
              },
            },
          ],
          enableEntityBankAccount: true,
        },
      }}
      locale={{
        code: i18n.locale,
        messages: {
          Payables: 'Bill Pay',
          Counterpart: 'Customer',
          Sales: 'Invoicing',
        },
      }}
    >
      {children}
    </MoniteProviderBase>
  );
};

